mapper.select:
SELECT

FROM SYS_LANGUAGE_T T1
where VALID_FLAG=1 
AND T1.LAN_CODE = #{lanCode,jdbcType=VARCHAR} 
AND T1.LAN_TYPE = #{lanType,jdbcType=INTEGER}
	AND ROWNUM = 1

SELECT

FROM SYS_LANGUAGE_T T1
where VALID_FLAG=1 AND T1.LAN_ID = #{lanId,jdbcType=NUMERIC}

SELECT

FROM SYS_LANGUAGE_T T1
where VALID_FLAG=1 
	AND T1.LAN_CODE = #{lanCode,jdbcType=VARCHAR} 
	AND T1.LAN_VALUE = #{lanValue,jdbcType=VARCHAR}
	AND T1.LAN_TYPE = #{lanType,jdbcType=INTEGER}
	AND ROWNUM = 1

mapper.update:
declare
begin
UPDATE SYS_LANGUAGE_T SET LAN_CODE = #{item.lanCode,jdbcType=VARCHAR}, LAN_VALUE = #{item.lanValue,jdbcType=VARCHAR}, LAN_TYPE = #{item.lanType,jdbcType=INTEGER}, REMARKS = #{item.remarks,jdbcType=VARCHAR}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE where LAN_ID = #{item.lanId,jdbcType=NUMERIC};
end;

mapper.insert:
declare
begin
INSERT INTO SYS_LANGUAGE_T ( LAN_ID, LAN_CODE, LAN_VALUE, LAN_TYPE, REMARKS, ATTRIBUTE1, ATTRIBUTE2, ATTRIBUTE3, ATTRIBUTE4, ATTRIBUTE5, ATTRIBUTE6, ATTRIBUTE7, ATTRIBUTE8, ATTRIBUTE9, ATTRIBUTE10, ORDER_CODE, CREATED_USER, CREATED_DATE, UPDATE_LAST_USER, UPDATE_LAST_DATE, VALID_FLAG ) values ( SYS_LANGUAGE_S.NEXTVAL, #{item.lanCode,jdbcType=VARCHAR}, #{item.lanValue,jdbcType=VARCHAR}, #{item.lanType,jdbcType=INTEGER}, #{item.remarks,jdbcType=VARCHAR}, #{item.attribute1,jdbcType=VARCHAR}, #{item.attribute2,jdbcType=VARCHAR}, #{item.attribute3,jdbcType=VARCHAR}, #{item.attribute4,jdbcType=VARCHAR}, #{item.attribute5,jdbcType=VARCHAR}, #{item.attribute6,jdbcType=VARCHAR}, #{item.attribute7,jdbcType=VARCHAR}, #{item.attribute8,jdbcType=VARCHAR}, #{item.attribute9,jdbcType=VARCHAR}, #{item.attribute10,jdbcType=VARCHAR}, #{item.orderCode,jdbcType=INTEGER}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE, 1 );
end;
