mapper.update:
declare
begin
UPDATE SYS_USERS_T SET USER_ACCOUNT = #{item.userAccount,jdbcType=VARCHAR}, USER_NAME = #{item.username,jdbcType=VARCHAR}, USER_PASSWORD=#{item.password,jdbcType=VARCHAR}, USER_DESC = #{item.userDesc,jdbcType=VARCHAR}, ENABLED = #{item.userEnabled,jdbcType=INTEGER}, ISSYS = #{item.issys,jdbcType=INTEGER}, USER_DEPT = #{item.userDept,jdbcType=VARCHAR}, USER_DUTY = #{item.userDuty,jdbcType=VARCHAR}, SUB_SYSTEM = #{item.subSystem,jdbcType=VARCHAR}, ATTRIBUTE1 = #{item.attribute1,jdbcType=VARCHAR}, ATTRIBUTE2 = #{item.attribute2,jdbcType=VARCHAR}, ATTRIBUTE3 = #{item.attribute3,jdbcType=VARCHAR}, ATTRIBUTE4 = #{item.attribute4,jdbcType=VARCHAR}, ATTRIBUTE5 = #{item.attribute5,jdbcType=VARCHAR}, ATTRIBUTE6 = #{item.attribute6,jdbcType=VARCHAR}, ATTRIBUTE7 = #{item.attribute7,jdbcType=VARCHAR}, ATTRIBUTE8 = #{item.attribute8,jdbcType=VARCHAR}, ATTRIBUTE9 = #{item.attribute9,jdbcType=VARCHAR}, ATTRIBUTE10 = #{item.attribute10,jdbcType=VARCHAR}, VALID_START = SYSDATE, VALID_END = #{item.validEnd,jdbcType=DATE}, ORDER_CODE = #{item.orderCode,jdbcType=INTEGER}, UPDATE_LAST_USER = #{item.updateLastUser,jdbcType=NUMERIC}, UPDATE_LAST_DATE = SYSDATE where USER_ID = #{item.userId,jdbcType=NUMERIC};
end;

mapper.insert:
declare
begin
INSERT into SYS_USERS_T ( USER_ID , USER_ACCOUNT , USER_NAME , USER_PASSWORD , USER_DESC , ENABLED, ISSYS , USER_DEPT , USER_DUTY , SUB_SYSTEM , ATTRIBUTE1 , ATTRIBUTE2 , ATTRIBUTE3 , ATTRIBUTE4 , ATTRIBUTE5 , ATTRIBUTE6 , ATTRIBUTE7 , ATTRIBUTE8 , ATTRIBUTE9 , ATTRIBUTE10 , VALID_FLAG , VALID_START , VALID_END , ORDER_CODE , CREATED_USER , CREATED_DATE , UPDATE_LAST_USER, UPDATE_LAST_DATE ) values ( SYS_USERS_S.NEXTVAL, #{item.userAccount,jdbcType=VARCHAR}, #{item.username,jdbcType=VARCHAR}, #{item.password,jdbcType=VARCHAR}, #{item.userDesc,jdbcType=VARCHAR}, #{item.userEnabled,jdbcType=INTEGER}, #{item.issys,jdbcType=INTEGER}, #{item.userDept,jdbcType=VARCHAR}, #{item.userDuty,jdbcType=VARCHAR}, #{item.subSystem,jdbcType=VARCHAR}, #{item.attribute1,jdbcType=VARCHAR}, #{item.attribute2,jdbcType=VARCHAR}, #{item.attribute3,jdbcType=VARCHAR}, #{item.attribute4,jdbcType=VARCHAR}, #{item.attribute5,jdbcType=VARCHAR}, #{item.attribute6,jdbcType=VARCHAR}, #{item.attribute7,jdbcType=VARCHAR}, #{item.attribute8,jdbcType=VARCHAR}, #{item.attribute9,jdbcType=VARCHAR}, #{item.attribute10,jdbcType=VARCHAR}, 1, SYSDATE, #{item.validEnd,jdbcType=DATE}, #{item.orderCode,jdbcType=INTEGER}, #{item.createdUser,jdbcType=NUMERIC}, SYSDATE, #{item.updateLastUser,jdbcType=NUMERIC}, SYSDATE );
end;
